import React from "react";

let url = "http://localhost:5000";
export function Carrito(props) {
  let comprar = () => {
    alert("Compra realizada");
    console.log(props);
    fetch(url + "/comprar", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(props),
    })
      .then((resp) => resp.text())
      .then((data) => {
        alert(data);
        window.location.href = "http://localhost:3000/home";
      });
  };
  return (
    <div>
      <h1> Productos agregados al carrito </h1>
      <hr />
      {props.carrito.map((elem) => {
        return (
          <div key={elem.id}>
            <div>
              <strong>{elem.nombre}</strong>
            </div>
            <div>
              <strong>{'$'+ elem.precio}</strong>
            </div>
            <hr />
          </div>
        );
      })}
      <button type="button" class="btn btn-primary" onClick={{comprar , venta}}>
        {" "}
        Comprar{" "}
      </button>
    </div>
  );
}

function venta(){
    let datos = {
        id: document.getElementById('id').value,
        nombre: document.getElementById("nombre").value,
        cantidad: document.getElementById("cantidad").value,
        precio: document.getElementById("precio").value,
      };
      fetch(url +'/actualizarVenta', {
        method:'post',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify(datos)
      })
      .then(res => res.json())
      .then(data => actualizar(data))
      .catch(err => {let data = {id:"",nombre:"",cantidad: "", precio: "" }
            actualizar(data)
    })
  
}


  function actualizar(data) {

    document.getElementById("id").value = data.id;
    document.getElementById("nombre").value = data.nombre;
    document.getElementById("cantidad").value = data.cantidad;
    document.getElementById("precio").value = data.precio;
  
  
  }
