import React from "react";

document.querySelector("#btnSend").addEventListener("click", ModificarProductos);
document.querySelector("#btnSelect").addEventListener("click", select);

function habilitacion(nombre,cantidad,precio,){

    document.getElementById('nombre').disabled = nombre
    document.getElementById('cantidad').disabled = cantidad
    document.getElementById('precio').disabled = precio

    
}
let option;
function select() {
  option = document.getElementById("selector").value;
  if (option == "Guardar") {
    document.getElementById("name").disabled = false;
    document.getElementById("cantidad").disabled = false;
    document.getElementById("precio").disabled = false;
    document.getElementById("btnSend").disabled = false;
  } else if (option == "Consultar") {
    document.getElementById("name").disabled = true;
    document.getElementById("cantidad").disabled = true;
    document.getElementById("precio").disabled = true;
    document.getElementById("btnSend").disabled = false;
  } else if (option == "Actualizar") {
    habilitacion(false, false, false, false);
  } else {
    habilitacion(false, true, true, true);
  }
}

let url = "http://localhost:5000";
export function ModificarProductos() {
  if (option == "Guardar") {
    let datos = {
      nombre: document.getElementById("name").value,
      apellido: document.getElementById("cantidad").value,
      edad: document.getElementById("precio").value,
    };
    fetch("/guardar", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(datos),
    })
      .then((resp) => resp.text())
      .then((data) => alert(data));
  } else if (option == "Consultar") {
    let datos = {
      id: document.getElementById("id").value,
    };
    fetch("/consultar", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(datos),
    })
      .then((res) => res.json())
      .then((data) => valores(data))
      .catch((err) => {
        alert("usuario no existe");
        let data = { nombre: "", apellido: "", edad: "" };
        valores(data);
      });
  } else if (option == "Actualizar") {
  } else {
  }

  return (
    <div>
      <form>
        <div>
          <select id="selector">
            <option>Guardar</option>
            <option>Consultar</option>
            <option>Actualizar</option>
            <option>Eliminar</option>
          </select>
          <button type="button" id="btnSelect">
            Seleccionar
          </button>
        </div>

        <div class="in">
          <label>Nombre</label>
        </div>
        <div class="in">
          <input type="text" id="nombre" disabled />
        </div>
        <div class="in">
          <label>Cantidad</label>
        </div>
        <div class="in">
          <input type="number" id="cantidad" disabled />
        </div>
        <div class="in">
          <input type="number" id="precio" disabled />
        </div>
        <div id="btn1">
          <button class="btn btn-success" type="button" onClick={() => ModificarProductos()}> Agregar</button> 
        </div>
      </form>
    </div>
  );

function habilitacion(nombre,cantidad,precio,){

    document.getElementById('nombre').disabled = nombre
    document.getElementById('cantidad').disabled = cantidad
    document.getElementById('precio').disabled = precio

    
}

function valores(data){
    document.getElementById('name').value = data.nombre
    document.getElementById('lastname').value = data.apellido
    document.getElementById('age').value = data.edad

}

}
