import React from "react";


function habilitacion(nombre,cantidad,precio,){

    document.getElementById('nombre').disabled = nombre
    document.getElementById('cantidad').disabled = cantidad
    document.getElementById('precio').disabled = precio

    
}
let option;

function select() {
  option = document.getElementById("selector").value;
  if (option == "Guardar") {
    document.getElementById("nombre").disabled = false;
    document.getElementById("cantidad").disabled = false;
    document.getElementById("precio").disabled = false;
  } else if (option == "Consultar") {
    document.getElementById("nombre").disabled = false;
    document.getElementById("cantidad").disabled = true;
    document.getElementById("precio").disabled = true;
  } else if (option == "Actualizar") {
    habilitacion(false, false, false, false);
  } else {
    habilitacion(false, true, true, true);
  }
}

let url = "http://localhost:5000";


export function ModificarProductos() {
  return (
    <div>
      <form>
        <div>
          <select id="selector">
            <option>Guardar</option>
            <option>Consultar</option>
            <option>Actualizar</option>
            <option>Eliminar</option>
          </select>

          <button class="btn btn-success" type="button" onClick={() => select()}> Seleccionar</button> 

        </div>

        <div class="in">
          <label>Nombre</label>
        </div>
        <div class="in">
          <input type="text" id="nombre" disabled />
        </div>
        <div class="in">
          <label>Cantidad</label>
        </div>
        <div class="in">
          <input type="number" id="cantidad" disabled />
        </div>
        <div class="in">
          <input type="number" id="precio" disabled />
        </div>
        <div id="btn1">
          <button class="btn btn-success" type="button" onClick={() => ModificarProductos()}> Enviar</button> 
        </div>
      </form>
    </div>
  );


}

if (option == "Guardar") {
    let datos = {
      nombre: document.getElementById("nombre").value,
      apellido: document.getElementById("cantidad").value,
      edad: document.getElementById("precio").value,
    };
    fetch("/guardarProducto", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(datos),
    })
      .then((resp) => resp.text())
      .then((data) => alert(data));
  } else if (option == "Consultar") {
    let datos = {
      nombre: document.getElementById("nombre").value,
    };
    fetch("/consultarProducto", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(datos),
    })
      .then((res) => res.json())
      .then((data) => valores(data))
      .catch((err) => {
        alert("usuario no existe");
        let data = { cantidad: "", precio: "" };
        valores(data);
      });
  } else if (option == "Actualizar") {
  } else {
  }

  function valores(data){
    document.getElementById('nombre').value = data.nombre
    document.getElementById('cantidad').value = data.cantidad
    document.getElementById('precio').value = data.precio

}
